mapper.select:
select a.*, b.questionnaire_description, c.user_name, d.convert_name, c.telphone_num as telphoneNum, e.convert_name as questionnaire_status_name from questionnaire_submit_information a left join (select * from questionnaire_information where del_flag = 0) b on a.questionnaire_id = b.id left join user_information c on a.user_id = c.id left join (select * from sys_dict where column_name = 'questionnaire_type') d on b.questionnaire_type = d.column_value left join (select * from sys_dict where column_name = 'questionnaire_status') e on a.questionnaire_status = e.column_value
AND c.telphone_num like concat('%',#{user.telphoneNum},'%')
AND c.user_name like concat('%',#{user.userName},'%')
AND a.questionnaireId = #{questionnaireId}
AND date_format(a.create_time ,'%Y-%m-%d') < #{queryEndDate}
AND date_format(a.create_time ,'%Y-%m-%d') > #{queryStartDate}
